import React, { useState } from 'react';

import ExpenseForm from './ExpenseForm';
import './NewExpense.css';

const NewExpense = (props) => {
  const [toAdd, setToAdd] = useState(false);

  const saveExpenseDataHandler = (enteredExpenseData) => {
    const expenseData = {
      ...enteredExpenseData,
      id: Math.random().toString(),
    };

    props.onAddExpense(expenseData);
  };

  const showExpenseFormHandler = () => {
    setToAdd((prevState) => !prevState);
  };

  let expenseForm = (
    <button onClick={showExpenseFormHandler}>Add New Expense</button>
  );

  if (toAdd) {
    expenseForm = <ExpenseForm onSaveExpenseData={saveExpenseDataHandler} />;
  }

  return <div className="new-expense">{expenseForm}</div>;
};

export default NewExpense;
